    /// - name: OAuth
    /// - parameter campaignId: (path) –ò–¥–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ç–æ—Ä –∫–∞–º–ø–∞–Ω–∏–∏ –≤ API –∏ –º–∞–≥–∞–∑–∏–Ω–∞ –≤ –∫–∞–±–∏–Ω–µ—Ç–µ. –ö–∞–∂–¥–∞—è –∫–∞–º–ø–∞–Ω–∏—è –≤ API —Å–æ–æ—Ç–≤–µ—Ç—Å—Ç–≤—É–µ—Ç –º–∞–≥–∞–∑–∏–Ω—É –≤ –∫–∞–±–∏–Ω–µ—Ç–µ.  –ß—Ç–æ–±—ã —É–∑–Ω–∞—Ç—å –∏–¥–µ–Ω—Ç–∏—Ñ–∏–∫–∞—Ç–æ—Ä—ã —Å–≤–æ–∏—Ö –º–∞–≥–∞–∑–∏–Ω–æ–≤, –≤–æ—Å–ø–æ–ª—å–∑—É–π—Ç–µ—Å—å –∑–∞–ø—Ä–æ—Å–æ–º [GET campaigns](../../reference/campaigns/getCampaigns.md).  ‚ÑπÔ∏è [–ß—Ç–æ —Ç–∞–∫–æ–µ –∫–∞–±–∏–Ω–µ—Ç –∏ –º–∞–≥–∞–∑–∏–Ω –Ω–∞ –ú–∞—Ä–∫–µ—Ç–µ](https://yandex.ru/support/marketplace/account/introduction.html)  
    /// - parameter putSkuBidsRequest: (body) description 
    /// - returns: AnyPublisher<EmptyApiResponse, Error> 
    open func putBidsForCampaign(campaignId: Int64, putSkuBidsRequest: PutSkuBidsRequest) -> AnyPublisher<EmptyApiResponse, Error> {
        Deferred {
            Result<URLRequest, Error> {
                guard let baseURL = self.transport.baseURL ?? self.baseURL else {
                    throw OpenAPITransportError.badURLError()
                }
                var localVarPath = "/campaigns/{campaignId}/bids"
                localVarPath = localVarPath.replacingOccurrences(of: "{campaignId}", with: "\(campaignId)")
                let localVarURL = baseURL.appendingPathComponent(localVarPath)
                let components = URLComponents(url: localVarURL, resolvingAgainstBaseURL: false)
                guard let requestURL = components?.url else {
                    throw OpenAPITransportError.badURLError()
                }
                var request = URLRequest(url: requestURL)
                request.httpMethod = "PUT"
                request.httpBody = try self.encoder.encode(putSkuBidsRequest)
                request.setValue("application/json", forHTTPHeaderField: "Content-Type")
                return request
            }.publisher
        }.flatMap { request -> AnyPublisher<EmptyApiResponse, Error> in 
            return self.transport.send(request: request)
                .mapError { transportError -> Error in 
                    if transportError.statusCode == 400 {
                        do {
                            let error = try self.decoder.decode(ApiClientDataErrorResponse.self, from: transportError.data)
                            return PutBidsForCampaignError.code400Error(error)
                        } catch {
                            return error
                        }
                    }
                    if transportError.statusCode == 401 {
                        do {
                            let error = try self.decoder.decode(ApiUnauthorizedErrorResponse.self, from: transportError.data)
                            return PutBidsForCampaignError.code401Error(error)
                        } catch {
                            return error
                        }
                    }
                    if transportError.statusCode == 403 {
                        do {
                            let error = try self.decoder.decode(ApiForbiddenErrorResponse.self, from: transportError.data)
                            return PutBidsForCampaignError.code403Error(error)
                        } catch {
                            return error
                        }
                    }
                    if transportError.statusCode == 404 {
                        do {
                            let error = try self.decoder.decode(ApiNotFoundErrorResponse.self, from: transportError.data)
                            return PutBidsForCampaignError.code404Error(error)
                        } catch {
                            return error
                        }
                    }
                    if transportError.statusCode == 420 {
                        do {
                            let error = try self.decoder.decode(ApiLimitErrorResponse.self, from: transportError.data)
                            return PutBidsForCampaignError.code420Error(error)
                        } catch {
                            return error
                        }
                    }
                    if transportError.statusCode == 500 {
                        do {
                            let error = try self.decoder.decode(ApiServerErrorResponse.self, from: transportError.data)
                            return PutBidsForCampaignError.code500Error(error)
                        } catch {
                            return error
                        }
                    }
                    return transportError
                }
                .tryMap { response in
                    try self.decoder.decode(EmptyApiResponse.self, from: response.data)
                }
                .eraseToAnyPublisher()
        }.eraseToAnyPublisher()
    }
