function _oacinternal_put_bids_for_campaign(_api::BidsApi, campaign_id::Int64, put_sku_bids_request::PutSkuBidsRequest; _mediaType=nothing)
    _ctx = OpenAPI.Clients.Ctx(_api.client, "PUT", _returntypes_put_bids_for_campaign_BidsApi, "/campaigns/{campaignId}/bids", ["OAuth", ], put_sku_bids_request)
    OpenAPI.Clients.set_param(_ctx.path, "campaignId", campaign_id)  # type Int64
    OpenAPI.Clients.set_header_accept(_ctx, ["application/json", ])
    OpenAPI.Clients.set_header_content_type(_ctx, (_mediaType === nothing) ? ["application/json", ] : [_mediaType])
    return _ctx
end

