    fun searchModelsRequestConfig(query: kotlin.String, regionId: kotlin.Long, currency: CurrencyType?, page: kotlin.Int?, pageSize: kotlin.Int?) : RequestConfig<Unit> {
        val localVariableBody = null
        val localVariableQuery: MultiValueMap = mutableMapOf<kotlin.String, kotlin.collections.List<kotlin.String>>()
            .apply {
                put("query", listOf(query.toString()))
                put("regionId", listOf(regionId.toString()))
                if (currency != null) {
                    put("currency", listOf(currency.toString()))
                }
                if (page != null) {
